DROP TABLE IF EXISTS `t_touristspots`;
CREATE TABLE `t_touristspots` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '唯一标识',
  `name` varchar(256) DEFAULT NULL COMMENT '景点名称',
  `address` varchar(256) DEFAULT NULL COMMENT '景点位置',
  `tel` varchar(256) DEFAULT NULL COMMENT '预定电话',
  `image` varchar(128) DEFAULT NULL COMMENT '展示图片',
  `introduce` text COMMENT '景点介绍',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 COMMENT='景点管理';

